import Layout from '../components/MyLayout';
import {useSelector, useDispatch} from 'react-redux';

export default function Account() {
	function Show() {
		const state = useDispatch();
		const isLoggedIn = state.users.current_user;

		if (isLoggedIn) {
			return <p>{state.users.current_user[0]}</p>
		}
		return <p>Log In</p>
	}

	return (
		<Layout>
			<Show />
		</Layout>
	);
}
