import Layout from '../components/MyLayout';
import { Formik } from 'formik';
import {useSelector, useDispatch} from 'react-redux';

export default function Login() {

	function Verify() {
		const users = useSelector(state => state.users.users);
		const isValidUser = this_product;

		if (isValidUser.length) {
			return (
				<div>
					<p>{this_product[0][0]}</p>
			 		<p>{this_product[0][1]}</p>
			 	</div>
			 	)
		}
		return True;
	}

	const dispatch = useDispatch();
	return (
		<Layout>
			<div>
				<Formik
					initialValues={{email: '', password: ''}}
					validate={values => {
						const errors = {};
						if (!values.email) {
							errors.email = 'Required'
						} else if (!values.password) {
							errors.password = 'Required'
						}
						return errors;
					}}
					onSubmit={(values, { setSubmitting }) => {
						setTimeout(() => {
							alert(JSON.stringify(values, null, 2));
							setSubmitting(true)
						}, 400);
					}}
				>
				{({
					values,
					errors,
					touched,
					handleChange,
					handleBlur,
					handleSubmit,
					isSubmitting,
				}) => (
					<form onSubmit={handleSubmit}>
						<input
							type="email"
							name="email"
							onChange={handleChange}
							onBlur={handleBlur}
							value={values.email}
						/>
						<input
							type="password"
							name="password"
							onChange={handleChange}
							onBlur={handleBlur}
							value={values.password}
						/>
						<button type="submit" onClick={() => dispatch({type: 'LOG_IN', payload: [values.email, values.password]})}>
							Log In
						</button>
					</form>
				)}
				</Formik>
			</div>
		</Layout>
	);
}
