import Layout from '../components/MyLayout';
import {useSelector, useDispatch} from 'react-redux';
import Product from '../components/Product';


	function Show() {
		const this_cart = useSelector(state => state.cart.cart)
		const arr = []
		let hash = new WeakMap();
		for (let i = 0; i < this_cart.length; i++) {
			if (hash[this_cart[i]] == null) {
				hash[this_cart[i]] = 1
			}
			else {
				hash[this_cart[i]] = hash[this_cart[i]] + 1
			}
		}

		for (var key in hash) {
			console.log(this_cart[0].title)
			console.log(key)
			let new_hash = {};
			new_hash.product = key;
			new_hash.quantity = hash[key];
			arr.push(new_hash);
			console.log(new_hash)
			console.log(new_hash.product)
			console.log(new_hash.product)
			// console.log(key);
			// console.log(hash[key])
		}
		// console.log(arr[0].product)
		// console.log(arr.map(product => <div>{product.title}</div>))
		return arr.map(product => <Product key={product.product} this_product={product.product}/>)
		// return <div>Hi</div>

	}

export default function Cart() {
	return (
		<Layout>
			<Show />
		</Layout>
	);
}
