import Layout from '../components/MyLayout';
import Product from '../components/Product';
import { connect } from 'react-redux';
import Client from 'shopify-buy';
import {useSelector, useDispatch} from 'react-redux';
import { bindActionCreators } from 'redux';
import { getProducts } from '../redux/actions/product_actions';

// import client from '../components/client';

// const productsPromise = client.product.fetchAll();
// const shopPromise = client.shop.fetchInfo();
let arr = []
const client = Client.buildClient({
	storefrontAccessToken: '26853603f3b67fead6e3bcf3ee602758',
	domain: 'a-very-small-store.myshopify.com'
})


client.product.fetchAll().then(res =>
	res.forEach(i => arr.push(i)
	)
);
console.log(client.product)
class Menu extends React.Component {
	constructor(props) {
		super(props);

		this.state = {
			products: []
		};
	}

	componentDidMount() {
		// console.log(this.props)

		client.product.fetchAll().then(res => {
			this.setState({
				products: [res]
			});
		});
	}


	// dispatch(getProducts())
	render() {
		return (
			<Layout>
				<p>Menu</p>
				<button onClick={this.props.getProducts}>Get Products</button>
			</Layout>
		);
	}
}

function mapStateToProps(state) {
	return {
		products: state.products.products
	}
}

function mapDispatchToProps(dispatch) {
	return {
		getProducts: () => dispatch(getProducts(client))
	};
}

// const mapDispatchToProps = (dispatch) => {
// 	return {
// 		getProducts: (client) => { dispatch({type: 'GET_PRODUCTS', payload: arr})}
// 	}
// }

// function mapDispatchToProps(dispatch) {
// 	return {
// 		actions: bindActionCreators(getProducts)
// 	};
// }


export default connect(mapStateToProps, mapDispatchToProps)(Menu);
