export default function usersReducer(state = {
	users: {'obafad94@gmail.com':'obafad94@gmail.com'},
	current_user: {}
}, action) {


	switch(action.type) {
		case 'GET_USERS':
			return {
				state
			}

		case 'ADD_USER':
			return {
				...state,
				users: {
					...state.users,
					[action.username]: action.password
				}
			}

		case 'LOG_IN':
			console.log('say it')
			return {
				...state,
				current_user: {
					[action.username]: action.password
				}
			}

		case 'LOG_OUT':
			return {
				...state,
				current_user: {}
			}
			
		default:
			return state;
	}
};
