import {GET_PRODUCTS} from "../action-types.js"
import {PRODUCT_IS_1} from "../action-types.js"

// export function productIs1() {
// 	return {type: PRODUCT_IS_1};
// }

export const getProducts = (client) => {
	return dispatch => {
		client.product.fetchAll().then(
			products => dispatch({
				type: GET_PRODUCTS,
				payload: products
			}))
	};
}

