// import {ADD_CLIENT} from '../actions/cartActions';

const cartReducer = (state = {products: []}, action) => {
	switch(action.type) {
		case ADD_CLIENT:
			client = action.payload
			return {
				...state,
				products: [action.payload]
			};
		default:
			return {...state}
	}
}

export default cartReducer;
