import Layout from '../components/MyLayout';
import Product from '../components/Product';
import { connect } from 'react-redux';
import Client from 'shopify-buy';


// import client from '../components/client';

// const productsPromise = client.product.fetchAll();
// const shopPromise = client.shop.fetchInfo();

const client = Client.buildClient({
	storefrontAccessToken: '26853603f3b67fead6e3bcf3ee602758',
	domain: 'a-very-small-store.myshopify.com'
})

// client.product.fetchAll().then(console.log);

class Menu extends React.Component {
	constructor(props) {
		super(props);

		this.state = {
			products: []
		};
	}

	componentWillMount() {
		client.product.fetchAll().then(res => {
			this.setState({
				products: [res]
			});
		});
	}


	render() {
		const {products} = this.props
		console.log(products)
		return (
			<Layout>
				<p>Menu</p>
			</Layout>
		);
	}
}

const mapStateToProps = state => {
	return {
		products: state.products
	}
}

export default connect(mapStateToProps)(Menu);
