import {ADD_CLIENT, ADD_ITEM, REMOVE_ITEM} from '../actions/counterActions';

export default function cartReducer(state = {
	cart_items: []
}, action) {

	switch(action.type) {

		case 'ADD_CLIENT':
			client = action.payload
			return {
				...state,
				cart_items: [
					...state.cart_items,
					action.payload
				]
			}

		case 'ADD_ITEM':
			return {
				...state,
				cart_items: [
					...state.cart_items,
					action.payload
				]
			}

		case 'REMOVE_ITEM':
			const cart_items = state.cart_items.filter(cart_item => cart_item.id !== action.payload.id)
			return {
				...state,
				cart_items
			}
			
		default:
			return state;
	}
};
